const actions = [];
[1,2,3].forEach(i => {
    const name = `action${i}`
    const id = flow.get(`${name}Id`);
    const title = env.get(`${name}Title`);
    const uri = env.get(`${name}Uri`);
    const action = !!uri.length ? 'URI' : title ? flow.get(`${name}Id`) : undefined;

    actions.push({
        action,
        title,
        uri
    });
});

msg._originalPayload = msg.payload;
flow.set('latestMessage', msg);

const services = env.get('service');
if(!services) {
    node.status({
        text: 'no services defined',
        shape: 'ring',
        fill: 'red'
    });
    return;    
}

services.trim().split(/,\s*/).forEach(service => {
    if(!service) return;

    msg.payload = {
        service,
        data: {
            title: msg.title,
            message: msg.message,
            data: {
                tag: flow.get('notificationTag'),
                actions,
                color: env.get("color"),
                group: env.get("group"),
                sticky: env.get("sticky"),
                timeout: env.get("timeout"),
                icon: env.get("icon")
            }
        }
    };
    node.send(msg);
});

node.done();

const latestMessage = flow.get('latestMessage');
const event = msg.payload.event;

latestMessage.eventData = msg.payload;
latestMessage.payload = latestMessage._originalPayload;
delete latestMessage._originalPayload;

if(env.get('userInfo')) {
    const userData = msg.userData.find(u => u.id === msg.payload.context.user_id);
    latestMessage.userData = userData;
}

if(msg.event_type === 'mobile_app_notification_cleared') {
    node.status({
        text: `cleared at: ${getPrettyDate()}`,
        shape: 'dot',
        fill: 'blue'
    });

    return [null, latestMessage];
}

const index = [1,2,3].find(i => event[`action_${i}_key`] === event.action);
node.status({
    text: `${event[`action_${index}_title`]} at: ${getPrettyDate()}`,
    shape: 'dot',
    fill: 'green'
});

return latestMessage;


function getPrettyDate() {
    return new Date().toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric',
        hour12: false,
        hour: 'numeric',
        minute: 'numeric',
    });
}

const services = env.get('service');
if(!services) {
    node.status({
        text: 'no services defined',
        shape: 'ring',
        fill: 'red'
    });
    return;    
}

services.trim().split(/,\s*/).forEach(service => {
    if(!service) return;

    msg.payload = {
        service,
        data: {
            message: "clear_notification",
            data: {
                tag: flow.get('notificationTag'),
            }
        }
    };
    node.send(msg);
});

node.done();

if (msg.title == null) { msg.title = env.get("title") }
if (msg.message == null) { msg.message = env.get("message") }
if (msg.action1 == null) { msg.action1 = env.get("action1Title") }
if (msg.action2 == null) { msg.action2 = env.get("action2Title") }
if (msg.action3 == null) { msg.action3 = env.get("action3Title") }
return msg;

var to_return = [null, null, null]
if ((msg.data.old_state.attributes.error == "Dust collector missing") || (msg.data.new_state.attributes.error == "Dust collector missing")) {
    // dust bin was emptied
    to_return[0] = msg 
}
if ((msg.data.old_state.attributes.mop_attached == false) || (msg.data.new_state.attributes.mop_attached == false)) {
    if (msg.mop_time >= 60) {
        // Check if mop was replaced
        to_return[1] = msg 
    }
    if (msg.tank_time > 0) {
        // consider tank refilled 
        to_return[2] = msg 
    }
}
return to_return

var min_area_for_credit = msg.area * 0.25 // at least do a quarter of the room. 
var max_area_for_credit = msg.area * 1.25 // If the room is bigger than 125%, something is wrong.
msg.cleaned_area = msg.data.new_state.attributes.cleaned_area
//if  (msg.cleaned_area >= max_area_for_credit) {
//    if (msg.data.new_state.attributes.status == "docking") {
//        // must have been a full clean. Reduce debris for every room.
//    } 
//} else if (msg.cleaned_area > msg.area) {
//    // Clutter or furniture was moved. Maybe resize room?
//}  else
if (msg.cleaned_area >= min_area_for_credit) {
    msg.reduce_by = msg.cleaned_area / msg.area * (1+0.25*(flow.get("vacuum_fan_speed")))
}    
return msg;

msg.new_level = Math.max(msg.payload.state - msg.reduce_by,0)
msg.new_level = Number(parseFloat(msg.new_level).toFixed(2))
return msg;

var message = msg.vacuum_friendly_name + " – "
var maintenance = 0

if (msg.data.new_state.attributes.error) {
    message = message + msg.data.new_state.attributes.error + " in " + flow.get(msg.vacuum_name + "_room").replace(/_/g, " ")
    maintenance++
}
// Log of this vacuum's bin needs emptying
if (msg.bin_time > 180) {
    if (maintenance > 0) { 
        message = message + ", "
    }
    message = message + "empty bin (" + msg.bin_time + "m)"
    maintenance++
}
// Log of this vacuum's tank needs refilling
if (msg.tank_time > 120) {
    if (maintenance > 0) { 
        message = message + ", "
    }
    message = message + "fill tank (" + msg.tank_time + "m)"
    maintenance++
} 
// Log of this vacuum's mop pad needs replacing
if (msg.mop_time > 180) { 
    if (maintenance > 0) { 
        message = message + ", "
    }
    message = message + "replace mop (" + msg.mop_time + "m)"
    maintenance++
} 
if (msg.payload.attributes.main_brush_left <= 1) { 
    if (maintenance > 0) { 
        message = message + ", "
    }
    message = message + "replace main brush"
    maintenance++
} 
if (msg.payload.attributes.side_brush_left <= 1) { 
    if (maintenance > 0) { 
        message = message + ", "
    }
    message = message + "replace side brush"
    maintenance++
} 
if (msg.payload.attributes.filter_left <= 1) { 
    if (maintenance > 0) { 
        message = message + ", "
    }
    message = message + "replace filter"
    maintenance++
} 
if (msg.payload.attributes.sensor_dirty_left <= 1) { 
    if (maintenance > 0) { 
        message = message + ", "
    }
    message = message + "clean sensors"
    maintenance++
} 
// If any maintenance is necessary, add it the name of the vacuuming 
// and add the whole thing to low priority messages
if (maintenance > 0) { 
    msg.vacuum_attn = message
} else {
    // have to pass something, and "undefined" gets removed from the messages
    msg.vacuum_attn = "undefined"
}
return msg;

if (msg.vacuum_attn != "") {
    msg.low_priority += msg.vacuum_attn
    msg.cleaning_report = msg.vacuum_attn + "<br />" + msg.cleaning_report
}
return msg;

// Variables
msg.new_level = msg.debris_level // Default to not changing the debris level for unsuccessful cleans
msg.percent_cleaned = Math.round(msg.cleaned_area / msg.area * 100)
// Check room size
if (msg.percent_cleaned > 125) {
    // Ask user if they want to resize the room.
    msg.state = "query-resize"
    msg.new_area = msg.cleaned_area    
} else if (msg.percent_cleaned > 100) {
    // Room is only slightly bigger, so resize automatically
     msg.state = "resize"
     msg.new_area = msg.cleaned_area
} 

// As long as soom of the room was cleaned, log debris change
if (msg.percent_cleaned >= 25) {
    // Debris is reduced by 0.01 for every 1% of the room cleaned (minimum 25%).
    // This value is multiplied by fan speed (80% on silent, 100% on standard, 120% on turbo)
    msg.clean_rate = Number(parseFloat(msg.percent_cleaned * (0.8+0.2*flow.get("vacuum_fan_speed")) / 100).toFixed(2))
    msg.new_level = Number(parseFloat(Math.max(msg.debris_level - msg.clean_rate,0)).toFixed(2))
}   else if ((msg.data.old_state.state == "cleaning") || (msg.data.old_state.state == "paused")) {
    // If returned or paused while cleaning and didn't do much, consider room a fail.
    // Debris level temporarily set to 0 to avoid repeated attempts.
    msg.new_level = 0
    msg.state = "failed"
} else {
    // Cleaned < 25% of room and 
    // cleaning was interrupted due to error. 
    // If it was a pause then return, normal check cycle should be okay.
    // if it was an error and return, normal check cycle should be okay.
    // Both are ok because it won't try to clean again immediately and the debris will be set to the same value.
}
return msg;

// Pull Vacuum name and room
msg.vacuum_name = msg.vacuum.replace("vacuum.","")
msg.room = flow.get(msg.vacuum_name + "_room")
// Determine cycle type
if (msg.room.match(/LOGGED$/g)) {
    // Vacuum "returning" a second time between cleaning
    msg.cycle = "logged"
} else if (msg.room.match(/\_floor$/g)) {
    // vacuum cleaned the entire floor
    msg.cycle = "floor"
    flow.set(msg.vacuum_name + "_room",msg.room + "_LOGGED")
} else {
    // Normal cycle
    msg.cycle = "normal"
    flow.set(msg.vacuum_name + "_room",msg.room + "_LOGGED")
}
// General Data
    msg.fan_speed = flow.get("vacuum_fan_speed")
    msg.cleaned_area = msg.data.new_state.attributes.cleaned_area
    msg.cleaned_time = msg.data.new_state.attributes.cleaning_time
    msg.mop_attached = msg.data.new_state.attributes.mop_attached
    msg.room_friendly = msg.room.charAt(0).toUpperCase() + msg.room.slice(1)
    msg.room_friendly = msg.room_friendly.replace(/_/g, " ");
return msg;

if (msg.mop_attached) {
    msg.mop_time += Number(msg.cleaned_time)
    msg.tank_time += Number(msg.cleaned_time)
}
msg.bin_time += Number(msg.cleaned_time)
return msg;

if (msg.state == "error") {
    msg.cleaning_report = msg.room_friendly + " – " + "Could not reach."
} else {
    msg.cleaning_report = msg.room_friendly + " – " + msg.percent_cleaned + "% cleaned – " + Math.round(msg.clean_rate*100) + " points"
}
return msg;

// Using the name of the  entity from the previous node, text is stripped
// away until we are left with a simple room name. 
msg.room = msg.room.entity_id.replace("_level","").replace("input_number.debris_","")
flow.set(msg.vacuum_name + "_room",msg.room)

// Figure out which segment(s). You will need to add the segment numbers manually.
// See the segment finder flow for information on determining the segments.
var segments = {
    // Upper level - Shadow
	"upper_hall": 16,
    "upper_office": 17,
    "bedroom": [20,22],
    "bathroom": 21,
    "guest_bathroom": 19,
    "guest_room": 0, // Not defined

    // Main level - Maxine
    "foyer": 17,
    "dining_room": 18,
    "living_room": 21,
    "kitchen_bar": 24,
    "kitchen_prep": 20,

    // Lower level - Rose
    "washroom": 2,
    "utility_hall": 18,
    "utility_room": 20,
    "family_room": 16,
    "office": 17
};
msg.segment = segments[msg.room]
return msg;

// Determines which vacuum is making the check. 
// If it is from the inject cycle, all vacuums are checked.

if (msg.vacuum_name) {
    // Remove parts from vacuum.[vacuum_name]
    msg.vacuum_name = msg.vacuum_name.replace("vacuum.","")
    // Remove parts from counter.[vacuum_name]_cleaning_queue
    msg.vacuum_name = msg.vacuum_name.replace("counter.","").replace("_cleaning_queue","")
    // Remove parts from sensor.debris_[vacuum_name]_next_room
    msg.vacuum_name = msg.vacuum_name.replace("sensor.debris_","").replace("_next_room","")
    msg.vacuum_name = msg.vacuum_name.replace("vacuum_","")
} else {
    // try each vacuum
    // Change me to your vacuum names! Leave out "vacuum." from the entity name
    var msg1 = { vacuum_name:"roborock_shadow" }; 
    var msg2 = { vacuum_name:"roborock_maxine" };
    var msg3 = { vacuum_name:"roborock_rose" };
    return [ [msg1, msg2, msg3] ];
} 
// Only 
// If this is from a cleaning queue, only trigger cleaning on the first click.
if (msg.queue) {
    if (msg.data.old_state.state == 0) {
        return msg;
    } else { 
        return null; 
    }
} else { return msg; }
